def sanitize_filename_keep_jp_punc(name: str, max_len: int) -> str:
    s = (name or "").replace("\r", " ").replace("\n", " ").strip()
    s = re.sub(r'[\\/:*?"<>|]', "_", s)  # Windows forbidden
    s = re.sub(r"\s+", " ", s).strip()
    s = s.rstrip(" .")
    if len(s) > max_len:
        s = s[:max_len].rstrip(" .")
    return s if s else "noname"


def resolve_result_path(result: str) -> str:
    if not result:
        return ""
    c1 = os.path.join("./files_server", result)
    c2 = os.path.join(".", result)
    if os.path.exists(c1):
        return os.path.abspath(c1)
    if os.path.exists(c2):
        return os.path.abspath(c2)
    if os.path.exists(result):
        return os.path.abspath(result)
    return ""


def move_with_collision_suffix(src_abs: str, dst_abs: str) -> str:
    os.makedirs(os.path.dirname(dst_abs), exist_ok=True)

    if not os.path.exists(dst_abs):
        shutil.move(src_abs, dst_abs)
        return dst_abs

    base, ext = os.path.splitext(dst_abs)
    n = 2
    while True:
        cand = f"{base}_{n}{ext}"
        if not os.path.exists(cand):
            shutil.move(src_abs, cand)
            return cand
        n += 1


def infer_prompt_language_from_text(text: str) -> str:
    if not text:
        return "ja"
    has_hangul = re.search(r"[가-힣]", text) is not None
    has_kana = re.search(r"[ぁ-ゟ゠-ヿ]", text) is not None
    has_cjk = re.search(r"[\u4e00-\u9fff]", text) is not None
    has_latin = re.search(r"[A-Za-z]", text) is not None
    if has_hangul:
        return "ko"
    if has_kana:
        return "ja"
    if has_cjk and not has_latin:
        return "zh"
    if has_latin and not has_cjk:
        return "en"
    return "ja"

#  N개씩 잘라서 합쳐서 반환 (총 4*N)
def build_audio_text_list_ja_N(N: int = 5) -> list:
    # 라벨별 문장 세트
    # - monolog: 괄호 독백（...）
    # - normal: 평서
    # - surprise: ！ 기반
    # - question: ？ 기반
    try:
        n = int(N)
    except Exception:
        n = 5

    # clamp
    if n < 1:
        n = 1
    if n > 5:
        n = 5

    monolog_pool = [
        "（うん……落ち着いて。まずは状況を整理しよう。）",
        "（今の反応、少し気になる……！もう一度確認した方がいいかも？）",
        "（ここは焦らず、ゆっくり進めるのが正解だよね。）",
        "（このまま行けば、きっと上手くいくはず……。）",
        "（先生、今ならちゃんと話せそう……。）",
    ]

    normal_pool = [
        "おはようございます、先生。今日も良い一日になりますように。",
        "新しいタスクを整理して……、このデータを確認しましたが、問題はなさそうです。",
        "新しいタスクを整理して、順番に進めましょう。",
        "システムの更新は完了しました。動作も安定しています。",
        "今日の進捗は順調です。この調子でいきましょう。",
    ]

    surprise_pool = [
        "えっ！今そんなことが起きたの！？",
        "おお！すごい！！これは予想以上に良い結果だよ！",
        "わっ！急に動いたからびっくりした！",
        "やった！ついに成功したね！",
        "すごい！こんなに早く終わるなんて！",
    ]

    question_pool = [
        "先生、今少し確認してもいいですか？",
        "次はどれから進めましょうか？この手順で合っていますか？？",
        "次はどれから進めましょうか？",
        "いまの結果は想定通りですか？",
        "このまま続けても大丈夫ですか？",
    ]

    audio_text_list = []
    audio_text_list.extend(monolog_pool[:n])
    audio_text_list.extend(normal_pool[:n])
    audio_text_list.extend(surprise_pool[:n])
    audio_text_list.extend(question_pool[:n])
    return audio_text_list


